
DROP TABLE inventory_totals;

CREATE TABLE `inventory_totals` (
  `product_id` bigint(20) NOT NULL,
  `location_id` bigint(20) NOT NULL,
  `total_qty` double DEFAULT '0',
  `created_at` datetime NOT NULL,
  `created_by` bigint(11) NOT NULL,
  `updated_at` datetime NOT NULL,
  `updated_by` bigint(11) NOT NULL,
  PRIMARY KEY (`product_id`,`location_id`),
  KEY `product_id` (`product_id`),
  KEY `location_id` (`location_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;




DROP TABLE inventory_total_details;

CREATE TABLE `inventory_total_details` (
  `product_id` bigint(20) NOT NULL,
  `location_id` bigint(20) NOT NULL,
  `total_cycle` double DEFAULT '0',
  `total_so` double DEFAULT '0',
  `total_pos` double DEFAULT '0',
  `total_cm` double DEFAULT '0',
  `total_to_in` double DEFAULT '0',
  `total_to_out` double DEFAULT '0',
  `date` date NOT NULL,
  `created_at` datetime NOT NULL,
  `created_by` bigint(11) NOT NULL,
  `updated_at` datetime NOT NULL,
  `updated_by` bigint(11) NOT NULL,
  PRIMARY KEY (`product_id`,`location_id`,`date`),
  KEY `product_id` (`product_id`),
  KEY `location_id` (`location_id`),
  KEY `date` (`date`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;







